import React,{useRef} from 'react';
import CanvasDraw from "react-canvas-draw";

const Home = props => {
	const canvasValue=useRef(null);
	const {router:{query}}=props;
	console.log(query)

	const handleClear=()=>{
		canvasValue.current.clear();
	}
	const handleUndo=()=>{
		canvasValue.current.undo();
	}
	const handleData=()=>{
		console.log(canvasValue.current.canvas.drawing.toDataURL("image/png"));
		// canvasValue.current
	}
	return(
	<div className="flex flex-grow-1 flex-column ph3">
		<div className="flex  mv3 ">
			<div className="">Logo</div>
			<div className="center flex br-pill ba">
				<span className=" bg-blue br--right3 pv2 ph4 pointer white">Learn</span>
				<span className="pv2 br--left3 ph4  pointer">Test</span>
			</div>	
		</div>
		<div className="flex flex-column mt4 ph4">
			<div className="flex mv3">
			<span className="br4 ba ph4 pv2 mr3 pointer">Alphabets</span>
			<span className="br4 ba ph4 pv2 pointer">Digit</span>
		</div>
		<div className="flex justify-between mv3">
			<div className="flex flex-column items-center w-25">
				<span className="mb2">Digits</span>
				<span className="ba f-6 flex h5 items-center justify-center w-100 ">A</span>
			</div>

			<div className="flex flex-column items-center justify-center">
				<button type="submit" className=" ph4 pv2 ba b--black mb3 pointer">Speak</button>
				<button type="submit" className=" ph4 pv2 ba b--black pointer" onClick={handleData}>Test</button>
			</div>
			<div className="flex flex-column items-center w-25">
				<span className="mb2">Canvas Draw</span>
				<CanvasDraw style={{height:'16rem',width:"100%",borderStyle: 'solid',borderWidth: '1px'}} brushColor="red" ref={canvasValue}/>
				<div className="flex mt3">
					<span className="mr3 ba ph3 pv2 pointer" onClick={handleClear}>Clear</span>
					<span className=" ba ph3 pv2 pointer" onClick={handleUndo}>Undo</span>
				</div>
			</div>
		</div>
		<button type="submit" className="absolute ba bottom-2 flex items-center justify-center ph4 pv2 right-2 w-10">Next</button>
		</div>	
	</div>
	)
}



export default Home;
