import React from 'react';
import CanvasDraw from "react-canvas-draw";

const Header = props => {

	return(

		<div className="flex  mv3 ">
			<div className="">Logo</div>
			<div className="center flex br-pill ba">
				<span className={`br--right3 pv2 ph4 pointer b--blue  ${props.type==='learn'?'bg-blue white':'blue'} `} data-type="learn" onClick={props.handleType}>Learn</span>
				<span className={`br--left3 pv2 ph4 pointer b--blue ${props.type==='test'?'bg-blue white':'blue'} `} data-type="test" onClick={props.handleType}>Test</span>
			</div>	
		</div>


	)
}



export default Header;
